mapper.insert:
insert into zk_records(z_id,r_name,r_user_id,create_time) values (#{zId},#{rName},#{rUserId},#{createTime});
insert into zk_text(z_id,r_id,text_val,x,y,rotate,scale,font_size,letter_spacing,font_weight,font_style,drag_type,mode,date_format,original_date,postpone,postpone_val,excel_arr) values (#{item.zId},#{item.rId},#{item.textVal},#{item.x},#{item.y},#{item.rotate},#{item.scale},#{item.fontSize},#{item.letterSpacing},#{item.fontWeight},#{item.fontStyle},#{item.dragType},#{item.mode},#{item.dateFormat},#{item.originalDate},#{item.postpone},#{item.postponeVal},#{item.excelArr});
insert into zk_counter_num(z_id, r_id, text_val, x, y, rotate, scale, font_size, letter_spacing, font_weight, font_style, drag_type, system, width, init_val, progress, min_val, max_val, repetition, high_order ) values (#{item.zId},#{item.rId},#{item.textVal},#{item.x},#{item.y},#{item.rotate},#{item.scale}, #{item.fontSize},#{item.letterSpacing},#{item.fontWeight},#{item.fontStyle},#{item.dragType} ,#{item.setCounterNum.system},#{item.setCounterNum.width},#{item.setCounterNum.initVal}, #{item.setCounterNum.progress},#{item.setCounterNum.minVal},#{item.setCounterNum.maxVal} ,#{item.setCounterNum.repetition},#{item.setCounterNum.highOrder});
insert into zk_image( z_id, r_id, x, y, image_src, width, height, file_id, origin_width, origin_height) values (#{item.zId},#{item.rId},#{item.x},#{item.y},#{item.imageSrc},#{item.imgStyle.width},#{item.imgStyle.height},#{item.imgStyle.fileId},#{item.originWidth},#{item.originHtight});
insert into zk_qrcode(z_id, r_id, x, y, drag_type, image_src, QC_text, QRCode_type, QC_text_type, QC_width, QC_height, file_id, set_variableQRCode) values (#{item.zId},#{item.rId},#{item.x},#{item.y},#{item.dragType},#{item.imageSrc}, #{item.setQRCode.QCText},#{item.setQRCode.QRCodeType},#{item.setQRCode.QCTextType},#{item.setQRCode.QCWidth} ,#{item.setQRCode.QCHeight},#{item.setQRCode.fileId},#{item.setQRCode.setVariableQRCode});

mapper.update:
update zk_records
r_name = #{rName},
where z_id = #{zId} and r_user_id = #{rUserId}
update zk_text
text_val = #{item.textVal}, x = #{item.x}, y = #{item.y}, rotate = #{item.rotate}, scale = #{item.scale}, font_size = #{item.fontSize}, letter_spacing = #{item.letterSpacing}, font_weight = #{item.fontWeight}, font_style = #{item.fontStyle}, drag_type = #{item.dragType}, mode = #{item.mode}, date_format = #{item.dateFormat}, original_date=#{item.originalDate}, postpone=#{item.postpone}, postpone_val=#{item.postponeVal}, excel_arr=#{item.excelArr},
where z_id = #{item.zId} and r_id = #{zId}
update zk_counter_num
text_val = #{item.textVal}, x = #{item.x}, y = #{item.y}, rotate = #{item.rotate}, scale = #{item.scale}, font_size = #{item.fontSize}, letter_spacing = #{item.letterSpacing}, font_weight = #{item.fontWeight}, font_style = #{item.fontStyle}, drag_type = #{item.dragType}, system = #{item.setCounterNum.system}, width = #{item.setCounterNum.width}, init_val = #{item.setCounterNum.initVal}, progress = #{item.setCounterNum.progress}, min_val = #{item.setCounterNum.minVal}, max_val = #{item.setCounterNum.maxVal}, repetition = #{item.setCounterNum.repetition}, high_order = #{item.setCounterNum.highOrder},
where z_id = #{item.zId} and r_id = #{zId}
update zk_image
x = #{item.x}, y = #{item.y}, img_src = #{item.imgSrc}, width = #{item.imgStyle.width}, height = #{item.imgStyle.height}, origin_width = #{item.originWidth}, origin_height = #{item.originHeight}, file_id = #{item.imgStyle.fileId},
where z_id = #{item.zId} and r_id = #{zId}
update zk_qrcode
QC_text = #{item.setQRCode.QCText}, QRCode_type = #{item.setQRCode.QRCodeType}, QC_text_type = #{item.setQRCode.QCTextType}, QC_width = #{item.setQRCode.QCWidth}, QC_height = #{item.setQRCode.QCHeight}, image_src = #{item.imageSrc}, x = #{item.x}, y = #{item.y}, drag_type = #{item.dragType}, file_id = #{item.setQRCode.fileId}, set_variableQRCode = #{item.setQRCode.setVariableQRCode},
where z_id = #{item.zId} and r_id = #{zId}

mapper.delete:
delete zk_records where z_id = #{rId} ;
delete zk_text where r_id = #{rId} ;
delete zk_counter_num where r_id = #{rId} ;
delete zk_image where r_id = #{rId} ;
delete zk_qrcode where r_id = #{rId} ;
